import React, { useState } from 'react';
import Formulaire from '../screens/Formulaire';

const FormulaireButton = () => {
    const [isFormOpen, setFormOpen] = useState(false);

    const openForm = () => {
        setFormOpen(true);
    };

    const closeForm = () => {
        setFormOpen(false);
    };

    return (
        <View style={styles.centeredView}>
            <View style={styles.button}>
                <Pressable
                    style={styles.button}
                    onPress={openForm}>
                    <Text style={styles.textStyle}>Ouvrir le formulaire</Text>
                </Pressable>
            </View>
            <Formulaire visible={isFormOpen} />
        </View>
    );
}
export default FormulaireButton;
