import { Text, View, TextInput, Button, Modal, StyleSheet } from "react-native"
import { useForm, Controller } from "react-hook-form"

const Formulaire = ({ formVisible, onCloseForm }) => {
  const {
    control,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({
    defaultValues: {
      tourEpaule: "",
      tourPoitrine: "",
      tourTaille: "",
      tourHanches: ""
    },
  })
  const onSubmit = (data) => {
    console.log(data)
    onCloseForm(false);
    reset();
  }


  return (
    <View>
      <Modal visible={formVisible}>
        <Controller
          control={control}
          rules={{
            required: true,
          }}
          render={({ field: { onChange, onBlur, value } }) => (
            <TextInput
              placeholder="Tour d'Ã©paule"
              onBlur={onBlur}
              onChangeText={onChange}
              value={value}
            />
          )}
          name="tourEpaule"
        />
        {errors.tourEpaule && <Text style={styles.innerText}>This is required.</Text>}

        <Controller
          control={control}
          rules={{
            required: true,
          }}
          render={({ field: { onChange, onBlur, value } }) => (
            <TextInput
              placeholder="Tour de poitrine"
              onBlur={onBlur}
              onChangeText={onChange}
              value={value}
            />
          )}
          name="tourPoitrine"
        />

        {errors.tourPoitrine && <Text style={styles.innerText}> This is required.</Text>}

        <Controller
          control={control}
          rules={{
            required: true,
          }}
          render={({ field: { onChange, onBlur, value } }) => (
            <TextInput
              placeholder="Tour de taille"
              onBlur={onBlur}
              onChangeText={onChange}
              value={value}
            />
          )}
          name="tourTaille"
        />

        {errors.tourTaille && <Text style={styles.innerText}>This is required.</Text>}

        <Controller
          control={control}
          rules={{
            required: true,
          }}
          render={({ field: { onChange, onBlur, value } }) => (
            <TextInput
              placeholder="Tour de hanches"
              onBlur={onBlur}
              onChangeText={onChange}
              value={value}
            />
          )}
          name="tourHanches"
        />

        {errors.tourHanches && <Text style={styles.innerText}>This is required.</Text>}

        <Button title="Submit" onPress={handleSubmit(onSubmit)} />
      </Modal>
    </View>
  )
}

const styles = StyleSheet.create({
  innerText: {
    color: 'red',
  },
})

export default Formulaire;
